function toggleParagraph(t){// Using event allows you to ge the dom element from the context of the click!
var e=t.srcElement.parentNode.nextElementSibling;"table-row"===e.style.display?(e.style.display="none",e.style.visibility="hidden"):(e.style.display="table-row",e.style.visibility="visible")}for(var dataTitles=document.getElementsByClassName("data-title"),dataContent=document.getElementsByClassName("data-content"),i=0;i<dataTitles.length;i++)dataTitles[i].addEventListener("click",toggleParagraph,!1);for(var i=0;i<dataContent.length;i++)dataContent[i].style.display="none",dataContent[i].style.visibility="hidden";